mapper.update:
UPDATE ACG_INFOMATION
ACG_NAME = #{acgName,jdbcType=VARCHAR}, ACG_AUTHOR = #{acgAuthor,jdbcType=VARCHAR}, ACG_CATEGORY = #{acgCategory,jdbcType=VARCHAR}, EPISODE = #{episode,jdbcType=INTEGER}, COMPLETED = #{completed,jdbcType=VARCHAR}, WATCH_ABLE = #{watchAble,jdbcType=VARCHAR}, COUNTRY = #{country,jdbcType=VARCHAR}, CREATED_DATE = #{createdDate,jdbcType=TIMESTAMP}, LAST_UPDATED_DATE = #{lastUpdatedDate,jdbcType=TIMESTAMP}, MEMO = #{memo,jdbcType=VARCHAR},
WHERE ID = #{ id, jdbcType=INTEGER }

mapper.select:
SELECT

FROM ACG_INFOMATION


        ((ACG_NAME LIKE '%${keyword}%') OR (ACG_AUTHOR LIKE '%${keyword}%') OR (MEMO LIKE '%${keyword}%'))

     AND ACG_CATEGORY = #{acgCategory} 
     AND COMPLETED = #{completed} 
     AND WATCH_ABLE = #{watchAble} 
     AND COUNTRY = #{country} 



        ORDER BY ${pageInfo.sortField} ${pageInfo.sortOrder}

     ORDER BY ID ASC 


    limit #{pageInfo.recordStart}, #{pageInfo.pageSize}

SELECT
    COUNT(1)
FROM ACG_INFOMATION


        ((ACG_NAME LIKE '%${keyword}%') OR (ACG_AUTHOR LIKE '%${keyword}%') OR (MEMO LIKE '%${keyword}%'))

     AND ACG_CATEGORY = #{acgCategory} 
     AND COMPLETED = #{completed} 
     AND WATCH_ABLE = #{watchAble} 
     AND COUNTRY = #{country}
